import './Autoc.css';
import Autosuggest from 'react-autosuggest';
import { useState } from 'react';

const data = [
  { personaje: "Jair Messias Bolsonaro" },
  { personaje: "Andrés Manuel López Obrador" },
  { personaje: "Alberto Ángel Fernández" },
  { personaje: "Iván Duque Márquez" },
  { personaje: "Gabriel Boric Font" },
  { personaje: "José Pedro Castillo Terrones" },
  { personaje: "Guillermo Alberto Santiago Lasso Mendoza" },
  { personaje: "Luis Rodolfo Abinader Corona" },
  { personaje: "Alejandro Eduardo Giammattei Falla" },
  { personaje: "Carlos Andrés Alvarado Quesada" },
];

function Autocomplete() {
  const[presidentes, setPresidentes]= useState(data);
  const[value, setValue]= useState("");
  const[presidenteSeleccionado, setPresidenteSeleccionado]= useState({});

  const onSuggestionsFetchRequested=({value})=>{
    setPresidentes(filtrarPresidentes(value));
  }

  const filtrarPresidentes=(value)=>{
    const inputValue=value.trim().toLowerCase();
    const inputLength=inputValue.length;

    var filtrado=data.filter((presidente)=>{
      var textoCompleto=presidente.presidente;

      if(textoCompleto.toLowerCase()
          .normalize("NFD")
          .replace(/[\u0300-\u036f]/g, "")
          .includes(inputValue)){
        return presidente;
      }
    });

    return inputLength===0 ? [] : filtrado;
  }

  const onSuggestionsClearRequested = () =>{
    setPresidentes([]);
  }

  const getSuggestionValue=(suggestion)=>{
    return `${suggestion.presidente}`;
  }

  const renderSuggestion=(suggestion)=>(
      <div className='sugerencia' onClick={()=>seleccionarPresidente(suggestion)}>
        {`${suggestion.presidente}`}
      </div>
  );

  const seleccionarPresidente=(presidente)=>{
    setPresidenteSeleccionado(presidente);
  }

  const onChange=(e, {newValue})=>{
    setValue(newValue);
  }

  const inputProps={
    placeholder:"País o Nombre del Presidente",
    value,
    onChange
  };

  const eventEnter=(e)=>{
    if(e.key == "Enter"){
      var split = e.target.value.split('-');
      var presidente ={
        presidente: split[0].trim(),
      };
      seleccionarPresidente(presidente);
    }
  }

  return (
      <div className="App">
        <Autosuggest
            suggestions={presidentes}
            onSuggestionsFetchRequested={onSuggestionsFetchRequested}
            onSuggestionsClearRequested={onSuggestionsClearRequested}
            getSuggestionValue={getSuggestionValue}
            renderSuggestion={renderSuggestion}
            inputProps={inputProps}
            onSuggestionSelected={eventEnter}
        />

      </div>
  );
}

export default Autocomplete;
