// ejercicio de counterApp.js

import React, { useState} from "react";
import './style.css';

const CounterAppUseState = () => {

    // el useState va a ser un objeto
    const [ state, setState] = useState({
        counter1: 10,
        counter2: 20,
        counter4: 30,
        counter5: 40,
    })

    // desestructuración
    const { counter1, counter2 } = state;

    return (
        <>
            <h1> Counter1 { counter1 } </h1>
            <h1> Counter1 { counter2 } </h1>

            <button className="btn btn-primary"
                    onClick={ () => {
                        setState( {
                            ...state,
                            counter1: counter1 + 1
                        });
                        // ... state --> se utiliza el spread operator dentro del objeto para no perder lo que ya estaba definido
                        // se llama al setStaten y le decimos: mantén lo que ya estaba y además suma 1

                    }}>
                +1
            </button>
        </>
    )}

export default CounterAppUseState;
