import React, {useState} from "react";
import './Formulario.css';
import { calcularTotal } from "../helpers";

const Formulario = ({cantidad,guardarCantidad, plazo, guardarPlazo}) => {

    // Definir el state
    // tenemos un state para un mensaje de error
    // se inicializa en false porque no hay error hasta que el usuario hago algo mal
    const [error, guardarError] = useState('false');

    const leerCantidad = (e) => {
        guardarCantidad( parseInt( e.target.value ));
    }



    const leerPrestamos = (e) => {
        guardarPlazo( parseInt( e.target.value ) )
    }



    // Creamos la función: cuando el usuario hace submit
    // e de evento
    const calcularPrestamo = e => {
        e.preventDefault();
        console.log('enviando...');

        // Validar
        if( cantidad === 0 || plazo === '') {
            console.log('error');
            guardarError(true); // en caso de que haya un error, pasa a true (se inicializa en false)
            return; // Resultado: Hooks: state: true
        } else {
            console.log('ok');
            guardarError(false);
        }

        // Realizar la cotización
        calcularTotal(cantidad,plazo);

    }


    return (
        <div>
            <form onSubmit={calcularPrestamo}> //evento cuando el usuario presiona el botón de enviar los datos
                <p>La cantidad es de {cantidad}</p>
                <p>El plazo es de {plazo}</p>
                {/*Resultado: 0, porque es el valor inicial del state*/}
                <div className="row">
                    <div>
                        <label>Cantidad Prestamo</label>
                        <input className="u-full-width"
                               type="number"
                               placeholder="Ejemplo: 3000"

                            // es un evento:
                            // leerCantidad es una función que se va a ejecutar
                               onChange={ leerCantidad }
                        />
                    </div>
                    <div>
                        <label>Plazo para Pagar</label>
                        <select
                            className="u-full-width"
                            onChange={ leerPrestamos }
                        >
                            <option value="">Seleccionar</option>
                            <option value="3">3 meses</option>
                            <option value="6">6 meses</option>
                            <option value="12">12 meses</option>
                            <option value="24">24 meses</option>
                        </select>
                    </div>
                    <div>
                        <input type="submit"
                               value="Calcular"
                               className="button-primary u-full-width"/>
                    </div>
                </div>
            </form>

            {/*si error está en true, sale el mensaje, sino no aparece nada, está vacío*/}
            { (error === true) ? <p>Todos los campos son obligatorios</p> : ''}

        </div>
    );
}

export default Formulario;
