// hook: useRef
// sirve para manejar el valor de alguna variable
// no dispara renderizaciones

import React, {useRef} from "react";
import '../04_00_hooks/style.css'

const FocusScreen = () => {

    // valor inicial vacío
    // permite mantener alguna referencia
    const inputRef = useRef();

    // función
    const handleClick = () => {
        // primera forma: buscar el input
        // pone el foco y selecciona el input
        // si hay varios, va a coger el primero
        // document.querySelector('input').select();

        // segunda forma
        // current es el valor actual
        // current es lo que imprime el useRef en consola
        inputRef.current.select();
    }

    return (
        <div>
            <h1> useRef - Focus Screen </h1>
            <hr/>

            {/*  ref se pone como referencia al input */}
            <input
                ref={ inputRef }
                type="text"
                className="form-control"
                placeholder="Nombre"
            />

            <button
                className="btn btn-outline-primary mt-5"
                onClick={ handleClick }
            >
                Focus
            </button>

        </div>
    );
};
export default FocusScreen;
