// se crea un custom hook que nos ayude a manejar el formulario
// va a ser: hooks/useForm.js

import React, {useEffect} from "react";
import './style.css'
import {useForm} from "../../hooks/useForm";

const SimpleFormCustom = () => {

    // se cambia el useState por el useForm
    // se le pasa formValues y la función handleInputChange
    const [formValues, handleInputChange] = useForm({
        name: '',  // objeto vacío,   es el name que aparece en en input, más abajo
        email: '',
        password: ''
    });

    const { name, email, password } = formValues;

    // esta función se coloca en el custom hook
    // se puede extraer la lógica
    // así se podría implementar la función en varios formularios
    // const handleInputChange = ({ target }) => {
    //     setFormState({
    //         ...formState,
    //         [ target.name ]: target.value
    //     });
    //
    // }


    // efecto que escucha cuando el email cambió
    useEffect( () => {
        console.log(' email cambió ')
    }, [ email ]);


    return (

        // se actualiza del fragment a form
        // el submit es la función handleInputChange
        <form onSubmit={handleInputChange}>
            <p> Simple Form CUSTOM Hook </p>
            <hr/>

            <div className="form-group">
                <input type="text"
                       name="name"
                       className="form-control"
                       placeholder="Tu nombre"
                       autoComplete="off"
                       value= { name }
                       onChange={ handleInputChange }
                />
            </div>
            <div className="form-group">
                <input type="text"
                       name="email"
                       className="form-control"
                       placeholder="email@gmail.com"
                       autoComplete="off"
                       value= { email }
                       onChange={ handleInputChange }
                />
            </div>
            <div className="form-group">
                <input type="password"
                       name="password"
                       className="form-control"
                       placeholder="*****"
                       value= { password }
                       onChange={ handleInputChange }
                />
            </div>
        </form>
    );
};
export default SimpleFormCustom;
