reportWebVitals();



const getHeroe = (id) => {
    return new Promise( (resolve, reject) => {
        setTimeout( () => {
            // resolve();

            const heroe = getHeroeById( id ); // ejemplo
            console.log(heroe)
        }, 2000)
    });
}


// qué hará cuando la promesa se cumpla

