import React, {useState, Component} from "react";
import logo from './logo.svg';
import './App.css';
import Header from "./Components/Header";
import Header01 from "./Components/Header01";
import Formulario from "./Components/Formulario";
import Mensaje from "./Components/Mensaje";
import Resultado from "./Components/Resultado";
import Spinner from "./Components/Spinner";


function App() {

    // En React los valores fluyen del padre al hijo pero no al revés,
    // se define un state para la cantidad
    // El state retorna dos valores
    // cantidad es: una variable que contiene el valor
    // guardarCantidad es: una función que va ser utilizada para estar interactuando y guardando lo que es el state que estamos creando
    // al useState se le puede dar un valor inicial. empieza en cero

    const [cantidad, guardarCantidad ] = useState(0);
    // const [plazo, guardarPlazo] = useState(0);
    const [plazo, guardarPlazo] = useState(''); // se le indica que es un string vacío
    // Plazo contiene el valor
    // guardarPlazo: es la función
    // pasamos ambas al formulario
    const [total, guardarTotal] = useState(0);

    // Spinner
    // se inicia en false
    const [cargando, guardarCargando] = useState(false);


    let componente;

    if(cargando) {
        componente = <Spinner/>
    } else if ( total === 0 ) {
        componente = <Mensaje />
    } else {
        componente = <Resultado
            total = {total}
            plazo = {plazo}
            cantidad = {cantidad}
        />
    }



    //Boolean
    class Boolean extends Component {
        render () {
            const textoBooleans = this.props.isActivated ? 'On!' : 'Off'

            return (
                <div>
                    <p>{textoBooleans}</p>
                </div>
            )
        }
    }

    //Números
    class Numbers extends Component {
        render () {
            const mappedNumber = this.props.arrayOfNumber.map(n => n * 2)

            return (
                <div>
                    <p>{this.props.arrayOfNumber.join(', ')}</p>
                    <p>{this.props.objectWithInfo.key}</p>
                </div>
            )
        }
    }


    return (
        <div className="App">

            <div className="App-header">

                {/* Podemos pasarle una props llamada isActivated
                    se suelen separar dejando una props en cada línea
                    si hay muchas, se ordenan alfabéticamente     */}
                <Boolean
                    text='prueba con booleanos'
                    isActivated={false} />
                    si a "isActivated" no se le pasa 


                <Numbers
                    arrayOfNumber={[2 ,3, 10]}
                    objectWithInfo={{ key: 'value', key2: 'otherValue' }}
                    text='Números'/>
            </div>



            {/* El componente de Header se puede poner así:*/}
            <Header/>
            {/*  también así para incluir los props  */}
            {/*  "título" es la propiedad, se le asigna un valor: "Prueba uno" */}
            <Header
                titulo="Header 1"
                descripcion="Utiliza el formulario y obten una cotización"/>
            <Header01
                titulo="Header 2"/>



            <Formulario
                cantidad={cantidad}
                guardarCantidad={guardarCantidad}
                plazo={plazo}
                guardarPlazo={guardarPlazo}
                total = {total}
                guardarTotal = {guardarTotal}
                guardarCargando = {guardarCargando}
            />


            {componente}


            <div className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <a className="App-link" href="https://www.20minutos.es/"
                   target="_blank" rel="noopener noreferrer">
                    periódico
                </a>
                <p>Hola mundo</p>
            </div>



        </div>
    );
}

export default App;
